mapper.select:
SELECT do.dictid, do.code, do.cname, do.ename FROM dictdef df,dictinfo do WHERE df.dictid = do.dictid AND df.dictid = #{dictid}
AND CODE >= (SELECT CODE FROM dictinfo WHERE cname = #{currentSalesStatus})
ORDER BY do.code

mapper.delete:
delete 
from dictinfo
where dictid = #{dictid}
  and code = #{code}

mapper.update:
update dictinfo
set dictid = #{newDictid,jdbcType=INTEGER}
where dictid = #{oldDictid,jdbcType=INTEGER}
