mapper.select:
select 

from t_act_project
where del_status ='0' and is_recommend='1'

  and  type_id = #{typeId,jdbcType=VARCHAR}

ORDER BY recommend desc,create_time desc
limit #{page},#{limit}

SELECT pro.id, pro.name, pro.img_url, pro.recommend, pro.is_recommend, (SELECT COUNT(*) FROM t_act_project_case AS pro_case WHERE pro_case.m_id=pro.id and pro_case.del_status ='0' ) AS caseNum, (SELECT COUNT(*) FROM t_act_project_order AS pro_cof WHERE pro_cof.m_id=pro.id and pro_cof.del_status ='0' ) AS orderNum, (SELECT COUNT(*) FROM t_act_project_config AS pro_con WHERE pro_con.m_id=pro.id) AS configNum FROM t_act_project AS pro where del_status ='0'
and pro.type_id=#{typeId,jdbcType=VARCHAR}
ORDER BY pro.recommend desc,pro.create_time desc

SELECT id, name, type_id, img_url, des, class_id, grade_id, t_new.case_ids, t_new.case_images, t_new.case_names FROM t_act_project as pro LEFT JOIN ( SELECT m_id, GROUP_CONCAT(id) as case_ids, GROUP_CONCAT(img_url) as case_images, GROUP_CONCAT(name) as case_names FROM t_act_project_case pro_c where pro_c.del_status='0' GROUP BY m_id ) as t_new ON pro.id = t_new.m_id where del_status ='0'
and pro.type_id = #{typeId,jdbcType=VARCHAR}
and pro.class_id like CONCAT('%',#{classId,jdbcType=VARCHAR},'%')
and pro.grade_id like CONCAT('%',#{gradeId,jdbcType=VARCHAR},'%')
and pro.name like CONCAT('%',#{name,jdbcType=VARCHAR},'%')
ORDER BY pro.recommend desc ,pro.create_time desc limit #{page},#{limit}
