mapper.select:
select 

from t_act_project_order
where m_id = #{mId,jdbcType=VARCHAR}
and   del_status ='0'
limit #{page},#{limit}

SELECT o.id, o.number, o.m_id, o.user_id, o.num, o.config_id, o.total_price, o.number_price, o.escort_number, o.escort_total_price, o.escort_price, o.total, o.user_name, o.phone, o.date_time, o.sort, t.name tName, p.name pName, u.user_name uName FROM t_act_project_order o LEFT JOIN t_act_project p ON p.id = o.m_id LEFT JOIN t_act_project_type t ON t.id = p.type_id LEFT JOIN t_act_user u ON o.user_id = u.id where o.del_status ='0'
and o.m_id= #{mId,jdbcType=VARCHAR}
and t.id=#{typeId,jdbcType=VARCHAR}
and p.name like CONCAT('%',#{name,jdbcType=VARCHAR},'%')
ORDER BY o.create_time desc

SELECT o.id, o.number, o.m_id, o.user_id, o.num, o.config_id, o.total_price, o.number_price, o.escort_number, o.escort_total_price, o.escort_price, o.total, o.user_name, o.phone, o.date_time, o.sort, t.name tName, p.name pName, p.img_url img_url, u.user_name uName FROM t_act_project_order o LEFT JOIN t_act_project p ON p.id = o.m_id LEFT JOIN t_act_project_type t ON t.id = p.type_id LEFT JOIN t_act_user u ON o.user_id = u.id where o.del_status ='0' and o.user_id=#{userId,jdbcType=VARCHAR}
and t.id=#{typeId,jdbcType=VARCHAR}
ORDER BY o.create_time desc limit #{page},#{limit}
